CREATE TABLE area (id BIGINT AUTO_INCREMENT, name VARCHAR(40) NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE carreta (id BIGINT AUTO_INCREMENT, placa VARCHAR(8) NOT NULL, peso FLOAT(18, 2) NOT NULL, configuracion VARCHAR(10) NOT NULL, propia TINYINT(1) NOT NULL, tara BIGINT NOT NULL, tipo_unidad VARCHAR(15) NOT NULL, empresa_id BIGINT NOT NULL, INDEX empresa_id_idx (empresa_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE chofer (id BIGINT AUTO_INCREMENT, name VARCHAR(40) NOT NULL, telefono BIGINT NOT NULL, brevete BIGINT NOT NULL, empresa_id BIGINT NOT NULL, INDEX empresa_id_idx (empresa_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE empresa (id BIGINT AUTO_INCREMENT, name VARCHAR(40) NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE lugar (id BIGINT AUTO_INCREMENT, tipo_lugar_id BIGINT NOT NULL, name VARCHAR(40) NOT NULL, INDEX tipo_lugar_id_idx (tipo_lugar_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE motivo (id BIGINT AUTO_INCREMENT, name VARCHAR(40) NOT NULL, tipo TINYINT(1) NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE operacion (id BIGINT AUTO_INCREMENT, registro_id BIGINT NOT NULL, supervisor_id BIGINT NOT NULL, observacion TEXT, tipo TINYINT NOT NULL, termino_at DATETIME NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX supervisor_id_idx (supervisor_id), INDEX registro_id_idx (registro_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE registro (id BIGINT AUTO_INCREMENT, empresa_id BIGINT NOT NULL, chofer_id BIGINT NOT NULL, tracto_id BIGINT NOT NULL, carreta_id BIGINT, lugar_id BIGINT NOT NULL, tipo_carga_id BIGINT NOT NULL, motivo_id BIGINT NOT NULL, cantidad BIGINT NOT NULL, guia1 VARCHAR(15) NOT NULL, guia2 VARCHAR(15), estado TINYINT NOT NULL, observacion TEXT, ingreso_at DATETIME NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX empresa_id_idx (empresa_id), INDEX chofer_id_idx (chofer_id), INDEX tracto_id_idx (tracto_id), INDEX carreta_id_idx (carreta_id), INDEX lugar_id_idx (lugar_id), INDEX tipo_carga_id_idx (tipo_carga_id), INDEX motivo_id_idx (motivo_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE salida (id BIGINT AUTO_INCREMENT, registro_id BIGINT NOT NULL, chofer_id BIGINT NOT NULL, tracto_id BIGINT NOT NULL, carreta_id BIGINT NOT NULL, tipo_carga_id BIGINT NOT NULL, lugar_id BIGINT NOT NULL, cantidad BIGINT, guia1 VARCHAR(15) NOT NULL, guia2 VARCHAR(15) NOT NULL, observacion TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX registro_id_idx (registro_id), INDEX chofer_id_idx (chofer_id), INDEX tracto_id_idx (tracto_id), INDEX carreta_id_idx (carreta_id), INDEX lugar_id_idx (lugar_id), INDEX tipo_carga_id_idx (tipo_carga_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE supervisor (id BIGINT AUTO_INCREMENT, sf_guard_user_id INT, name VARCHAR(40) NOT NULL, last_name VARCHAR(40) NOT NULL, email VARCHAR(60), INDEX sf_guard_user_id_idx (sf_guard_user_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE tipo_carga (id BIGINT AUTO_INCREMENT, area_id BIGINT NOT NULL, name VARCHAR(40) NOT NULL, INDEX area_id_idx (area_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE tipo_lugar (id BIGINT AUTO_INCREMENT, name VARCHAR(40) NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE tracto (id BIGINT AUTO_INCREMENT, placa VARCHAR(8) NOT NULL, peso FLOAT(18, 2) NOT NULL, configuracion VARCHAR(10) NOT NULL, propia TINYINT(1) NOT NULL, tara BIGINT NOT NULL, tipo_unidad VARCHAR(15) NOT NULL, empresa_id BIGINT NOT NULL, INDEX empresa_id_idx (empresa_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_group (id INT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_group_permission (group_id INT, permission_id INT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(group_id, permission_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_permission (id INT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_remember_key (id INT AUTO_INCREMENT, user_id INT, remember_key VARCHAR(32), ip_address VARCHAR(50), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id, ip_address)) ENGINE = INNODB;
CREATE TABLE sf_guard_user (id INT AUTO_INCREMENT, username VARCHAR(128) NOT NULL UNIQUE, algorithm VARCHAR(128) DEFAULT 'sha1' NOT NULL, salt VARCHAR(128), password VARCHAR(128), is_active TINYINT(1) DEFAULT '1', is_super_admin TINYINT(1) DEFAULT '0', last_login DATETIME, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX is_active_idx_idx (is_active), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_group (user_id INT, group_id INT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, group_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_permission (user_id INT, permission_id INT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, permission_id)) ENGINE = INNODB;
